INSERT INTO db_script_string (entry,content_default,sound,type,language,emote,comment) VALUES
(2000001119,'Hey! Stop rooting around in my cellar! Clear out!',0,0,0,5,'Michael Belfast - say_inn_1'),
(2000001120,'What were you doing in my cellar? There\'s a food scare going on, and the last thing I need is strangers rummaging around in my goods! Shoo!',0,0,0,5,'Michael Belfast - say_inn_2'),
(2000001121,'This whole situation seems a bit paranoid, don\'t you think?',0,0,0,6,'Hearthsinger Forresten - say_inn_2'),
(2000001122,'Orders are orders. If the Prince says jump...',0,0,0,1,'Footman James - say_inn_3'),
(2000001123,'It\'s a strange order, you can\'t deny. Suspicious food? Under that definition, you should arrest Belfast!',0,0,0,1,'Fras Siabi - say_inn_4'),
(2000001124,'I HEARD THAT! No more ale for you! Not a drop!',0,0,0,5,'Michael Belfast - say_inn_5'),
(2000001125,'Enough, Michael. Business is hurting enough with this scare as it is. We can use every copper.',0,0,0,1,'Mal Corricks - say_inn_6'),
(2000001126,'The soldiers are doing important work. The safety of the people is more important, Mal, if you\'re interested in your customers living to spend another day.',0,0,0,1,'Gryan Stoutmantle - say_inn_7'),
(2000001127,'%s grudgingly nods.',0,2,0,0,'Mal Corricks - emote_inn_1'),
(2000001128,'I can\'t argue with that.',0,0,0,1,'Mal Corricks - say_inn_8'),
(2000001129,'Don\'t worry too much. By the time I went off duty, we hadn\'t found a scrap of befouled grain here.',0,0,0,1,'Footman James - say_inn_9'),
(2000001130,'Thank the Light for that.',0,0,0,1,'Hearthsinger Forresten - say_inn_10'),
(2000001131,'%s nods.',0,2,0,0,'Fras Siabi - emote_inn_2'),
(2000001132,'Why, hello again!',0,0,0,0,'Chromie say_end_1');
