INSERT INTO db_script_string (entry,content_default,sound,type,language,emote,comment) VALUES
(2000001133,'Ok, enough work for now. Time for refreshments and a little conversation in the inn.',0,0,0,1,'Roger Owens - say_roger_1'),
(2000001134,'Wait, what\'s that smell?',0,0,0,1,'Roger Owens - say_roger_2'),
(2000001135,'Can\'t be me, I took a bath 3 days ago!',0,0,0,1,'Roger Owens - say_roger_3'),
(2000001136,'Oh, close call. It\'s just the grain here.',0,0,0,1,'Roger Owens - say_roger_4'),
(2000001137,'Wait a second. Grain isn\'t supposed to smell like THAT! I better go find a guard.',0,0,0,1,'Roger Owens - say_roger_5'),
(2000001138,'You don\'t mind me checking out your merchandise for signs of tampering, do you?',0,0,0,1,'Sergeant Morigan - say_morigan_1'),
(2000001139,'No, Sergeant.',0,0,0,0,'Silvio Perelli - say_morigan_2'),
(2000001140,'Wait, what is this? You\'ve been holding out on me, Perelli!',0,0,0,1,'Sergeant Morigan - say_morigan_3'),
(2000001141,'What are you talking about, Sergeant!',0,0,0,5,'Silvio Perelli - say_morigan_4'),
(2000001142,'I\'m confiscating this suspicious grain, Perelli. We were looking for signs of tampered food, and it would be in your best interest to stay put while Prince Arthas checks this out.',0,0,0,1,'Sergeant Morigan - say_morigan_5'),
(2000001143,'You have to believe me, I\'m innocent!',0,0,0,1,'Silvio Perelli - say_morigan_6'),
(2000001144,'We\'ll see about that, Perelli. We\'ll see about that.',0,0,0,1,'Sergeant Morigan - say_morigan_7');
