INSERT INTO db_script_string (entry,content_default,sound,type,language,emote,comment) VALUES
(2000001145,'Martha, I\'m out of flour for bread. You wouldn\'t happen to have any grain from that recent shipment, would you?',0,0,0,6,'Jena Anderson - say_jena_1'),
(2000001146,'Oh hello, Jena. Of course you can borrow some grain. Help yourself.',0,0,0,1,'Martha Goslin - say_jena_2'),
(2000001147,'Thanks, Martha! I owe you one.',0,0,0,0,'Jena Anderson - say_jena_3'),
(2000001148,'Martha, something\'s wrong with this grain! Some of the Prince\'s soldiers were looking for this. I\'m going to go look for one.',0,0,0,5,'Jena Anderson - say_jena_4'),
(2000001149,'Oh, dear.',0,0,0,1,'Jena Anderson - say_jena_5'),
(2000001150,'Oh, my.',0,0,0,1,'Martha Goslin - say_jena_6'),
(2000001151,'Looks like a storm\'s coming in, Scruffy...',0,0,0,0,'Malcolm Moore - say_malcolm_1'),
(2000001152,'%s begins to growl...',1108,2,0,0,'Scruffy - say_malcolm_2'),
(2000001153,'What\'s wrong, pal?',0,0,0,0,'Malcolm Moore - say_malcolm_3'),
(2000001154,'What did you find, boy?',0,0,0,16,'Malcolm Moore - say_malcolm_4'),
(2000001155,'This is no good, Scruffy. Stay here and guard the house, I need to go find a soldier.',0,0,0,0,'Malcolm Moore - say_malcolm_6'),
(2000001156,'This grain shipment has been nothing but trouble!',0,0,0,5,'Bartleby Battson - say_bartelby_1'),
(2000001157,'I\'m going to lose my on time bonus because of this!',0,0,0,5,'Bartleby Battson - say_bartelby_2'),
(2000001158,'I wasn\'t even supposed to be here today!',0,0,0,5,'Bartleby Battson - say_bartelby_3'),
(2000001159,'Well, guess I should load everything back into the cart.',0,0,0,0,'Bartleby Battson - say_bartelby_4'),
(2000001160,'Oh, come on! My cart broke, my horse lost a shoe, and now the cargo goes bad!',0,0,0,5,'Bartleby Battson - say_bartelby_5'),
(2000001161,'I guess I\'ll go find the athorities. If I\'m lucky they\'ll tell me it\'s the plague and that we\'re all going to die.',0,0,0,1,'Bartleby Battson - say_bartelby_6');
